import React, { useState } from 'react'

export default function TextForm() {

     function upHandller(){

        const newtext=text.toLocaleUpperCase();
        setText(newtext)
     }
     function loHandller(){

        const newtext=text.toLocaleLowerCase();
        setText(newtext)
     }

      function handletoChange(e){
        setText(e.target.value)


      }


    const [text,setText]=useState("Enter Your Text Here")
  return (
    <>
    

<div className='container my-5'>
 
 <h2>Enter Your Enelze Text</h2>
     
<div className="mb-3 my-3">

<textarea className="form-control my-3" id="mybox" rows="8" value={text}  onChange={handletoChange}/>
<button  className='btn btn-primary mx-2' onClick={upHandller}>ClickToUpperCase</button>
<button  className='btn btn-primary mx-2' onClick={loHandller}>ClickToLowerCase</button>

</div>
</div>
<div className='container my-5'>
    <h3> Your Text Summary</h3>
    <p><b>{text.split(" ").length}</b> Words, <b>{text.length}</b> Charecters</p>
    <p> {0.008 * text.split(" ").length} Minutes to read</p>
    <h3>Preview</h3>
    <p>{text}</p>
</div>
</>

  );
}
